import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import '../Consulting/consulting.css';

const ForeignDSCPage = () => {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      phone: '',
      country: '',
      message: '',
    },
    validationSchema: Yup.object({
      name: Yup.string()
        .matches(/^[a-zA-Z\s]{2,50}$/, 'Please enter a valid name (2-50 characters, letters only).')
        .required('Name is required'),
      email: Yup.string()
        .email('Invalid email address')
        .required('Email is required'),
      phone: Yup.string()
        .matches(/^\d{10,15}$/, 'Please enter a valid phone number (10-15 digits).')
        .required('Phone number is required'),
      country: Yup.string()
        .required('Country is required'),
      message: Yup.string()
        .min(10, 'Message should be at least 10 characters long and provide meaningful content.')
        .required('Message is required'),
    }),
    onSubmit: async (values, { setSubmitting, setErrors, resetForm }) => {
      try {
        const response = await axios.post('http://localhost:4000/foreigndsc', values);
        console.log(response.data.message);
        resetForm();
      } catch (err) {
        setErrors({ submit: 'Failed to send email. Please try again.' });
      } finally {
        setSubmitting(false);
      }
    },
  });

  return (
    <div className="foreign-dsc-page">
      <header className="foreign-dsc-header">
        <h1>Foreign DSC Services</h1>
      </header>
      <main className="foreign-dsc-content">
        <div className="foreign-dsc-main">
          <div className="foreign-dsc-info">
            <div className="foreign-dsc-text">
              <p>
                At Murari Solutions, we provide Digital Signature Certificate (DSC) services tailored for foreign clients. Whether you are an NRI or a foreign entity, our team will assist you in obtaining a DSC that complies with international standards and legal requirements.
              </p>
              <h1>Documents Required for Foreign DSC</h1>
              <p>
                Passport: Provide a copy of a valid passport, which should be notarized.
                Proof of Address: Submit a notarized copy of a utility bill, bank statement, or any official document showing your current address.
                Application Form: Fill out and sign the DSC application form available on our website.
                Photo: Provide a recent passport-sized photograph.
                Authorization Letter: If applying on behalf of a company, an authorization letter on the companyâ€™s letterhead is required.
              </p>
              <h3>
                Contact us today to start the process of obtaining your DSC and ensure smooth digital transactions globally.
              </h3>
            </div>
          </div>
          <div className="phone-frame">
            <div className="phone-screen">
              <div className="contact-form">
                <h2>Enroll Now</h2>
                <form onSubmit={formik.handleSubmit}>
                  <label aria-label="Name">
                    Name:
                    <input
                      type="text"
                      name="name"
                      value={formik.values.name}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.name && formik.errors.name ? (
                      <div className="error-message">{formik.errors.name}</div>
                    ) : null}
                  </label>
                  <label aria-label="Email">
                    Email:
                    <input
                      type="email"
                      name="email"
                      value={formik.values.email}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.email && formik.errors.email ? (
                      <div className="error-message">{formik.errors.email}</div>
                    ) : null}
                  </label>
                  <label aria-label="Phone">
                    Phone:
                    <input
                      type="tel"
                      name="phone"
                      value={formik.values.phone}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.phone && formik.errors.phone ? (
                      <div className="error-message">{formik.errors.phone}</div>
                    ) : null}
                  </label>
                  <label aria-label="Country">
                    Country:
                    <input
                      type="text"
                      name="country"
                      value={formik.values.country}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.country && formik.errors.country ? (
                      <div className="error-message">{formik.errors.country}</div>
                    ) : null}
                  </label>
                  <label aria-label="Message">
                    Message:
                    <textarea
                      name="message"
                      value={formik.values.message}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.message && formik.errors.message ? (
                      <div className="error-message">{formik.errors.message}</div>
                    ) : null}
                  </label>
                  <button type="submit" className="submit-btn" disabled={formik.isSubmitting}>
                    {formik.isSubmitting ? <div className="loading-spinner"></div> : 'Submit'}
                  </button>
                </form>
                {formik.errors.submit && <p className="error-message">{formik.errors.submit}</p>}
              </div>
            </div>
          </div>
        </div>
      </main>
      <div className="advisor">
        <button onClick={() => navigate('/services')} className="back-btn">Go back</button>
      </div>
    </div>
  );
};

export default ForeignDSCPage;
