import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import '../../services/Consulting/consulting.css';

const ConsultingPage = () => {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      phone: '',
      message: '',
    },
    validationSchema: Yup.object({
      name: Yup.string()
        .matches(/^[a-zA-Z\s]{2,50}$/, 'Please enter a valid name (2-50 characters, letters only).')
        .required('Name is required'),
      email: Yup.string()
        .email('Invalid email address')
        .required('Email is required'),
      phone: Yup.string()
        .matches(/^\d{10}$/, 'Please enter a valid 10-digit phone number.')
        .required('Phone number is required'),
      message: Yup.string()
        .min(10, 'Message should be at least 10 characters long and provide meaningful content.')
        .required('Message is required'),
    }),
    onSubmit: async (values, { setSubmitting, setErrors, resetForm }) => {
      try {
        // GraphQL endpoint and mutation query
        const response = await axios.post('http://localhost:4000/graphql', {
          query: `
            mutation {
              addEnrollment(name: "${values.name}", email: "${values.email}", phone: "${values.phone}", message: "${values.message}") {
                id
                name
                email
                phone
                message
              }
            }
          `,
        });
        console.log(response.data.data.addEnrollment);
        resetForm();
      } catch (err) {
        setErrors({ submit: 'Failed to send data. Please try again.' });
      } finally {
        setSubmitting(false);
      }
    },
  });

  return (
    <div className="consulting-page">
      <header className="consulting-header">
        <h1>Consulting Services</h1>
      </header>
      <main className="consulting-content">
        <div className="consulting-main">
          <div className="consulting-info">
            <div className="consulting-text">
              <p>At Murari Solutions, we offer expert business consulting services tailored to your needs...</p>
              <h1>Documents Needed for Consultancy Registration</h1>
              <p>Identity Proof: Provide copies of PAN card...</p>
              <h3>Enroll or contact us today to schedule a consultation and take the first step towards optimizing your business.</h3>
            </div>
          </div>
          <div className="phone-frame">
            <div className="phone-screen">
              <div className="contact-form">
                <h2>Enroll Now</h2>
                <form onSubmit={formik.handleSubmit}>
                  <label aria-label="Name">
                    Name:
                    <input
                      type="text"
                      name="name"
                      value={formik.values.name}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.name && formik.errors.name ? (
                      <div className="error-message">{formik.errors.name}</div>
                    ) : null}
                  </label>
                  <label aria-label="Email">
                    Email:
                    <input
                      type="email"
                      name="email"
                      value={formik.values.email}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.email && formik.errors.email ? (
                      <div className="error-message">{formik.errors.email}</div>
                    ) : null}
                  </label>
                  <label aria-label="Phone">
                    Phone:
                    <input
                      type="tel"
                      name="phone"
                      value={formik.values.phone}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.phone && formik.errors.phone ? (
                      <div className="error-message">{formik.errors.phone}</div>
                    ) : null}
                  </label>
                  <label aria-label="Message">
                    Message:
                    <textarea
                      name="message"
                      value={formik.values.message}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.message && formik.errors.message ? (
                      <div className="error-message">{formik.errors.message}</div>
                    ) : null}
                  </label>
                  <button type="submit" className="submit-btn" disabled={formik.isSubmitting}>
                    {formik.isSubmitting ? <div className="loading-spinner"></div> : 'Submit'}
                  </button>
                </form>
                {formik.errors.submit && <p className="error-message">{formik.errors.submit}</p>}
              </div>
            </div>
          </div>
        </div>
      </main>
      <div className="advisor">
        <button onClick={() => navigate('/accounting-services')} className="back-btn">Go back</button>
      </div>
    </div>
  );
};

export default ConsultingPage;
